scripts.test:
node --input-type=module -e "import { spawn } from 'node:child_process';
const vitestBin = process.platform === 'win32' ? 'node_modules/.bin/vitest.cmd' : 'node_modules/.bin/vitest';
const child = spawn(vitestBin, ['run', '--reporter', 'verbose'], { stdio: ['inherit', 'pipe', 'pipe'] });
const isStatusLine = (line) => /^\s*[✓√×✗xX]\s+/.test(line);
const keep = (line) => isStatusLine(line) || /^\s*[✓√]\s+tests\//.test(line) || /^\s*Test Files\s+/.test(line) || /^\s*Tests\s+/.test(line) || /^\s*✓\s+tests\//.test(line);
const drop = (line) => {
  if (!line.trim()) return true;
  if (line.includes('The CJS build of Vite')) return true;
  if (line.includes('vite.dev/guide/troubleshooting')) return true;
  if (line.includes('vite-cjs-node-api-deprecat')) return true;
  if (/^\s*d for more details\.\s*$/.test(line)) return true;
  if (/^\s*RUN\s+v\d+\.\d+\.\d+\b/.test(line)) return true;
  if (/^\s*Start at\s+/.test(line)) return true;
  if (/^\s*Duration\s+/.test(line)) return true;
  return false;
};
const pipeFiltered = (stream, write) => {
  let buf = '';
  stream.setEncoding('utf8');
  stream.on('data', (chunk) => {
    buf += chunk;
    let i;
    while ((i = buf.indexOf('\n')) !== -1) {
      const raw = buf.slice(0, i);
      buf = buf.slice(i + 1);
      const line = raw.endsWith('\r') ? raw.slice(0, -1) : raw;
      if (drop(line)) continue;
      if (keep(line) || line.includes(' FAIL ') || line.startsWith(' FAIL ') || line.includes('Error:') || line.includes('Unhandled') || line.includes('Unhandled Rejection')) {
        write(line + '\n');
      }
    }
  });
  stream.on('end', () => {
    if (!buf) return;
    const line = buf.endsWith('\r') ? buf.slice(0, -1) : buf;
    if (drop(line)) return;
    if (keep(line) || line.includes(' FAIL ') || line.startsWith(' FAIL ') || line.includes('Error:') || line.includes('Unhandled') || line.includes('Unhandled Rejection')) {
      write(line);
    }
  });
};
pipeFiltered(child.stdout, (s) => process.stdout.write(s));
pipeFiltered(child.stderr, (s) => process.stderr.write(s));
child.on('close', (code, signal) => {
  if (signal) process.exit(1);
  process.exit(code ?? 1);
});"
